class Solution:
    def findAnagrams(self,s,p):
        self.index = []
        start = 0
        while start + len(p) - 1 <= len(s) - 1:
            ss = s[start:start + len(p)]
            res = p
            for i in range(len(ss)):
                if ss[i] not in res:
                    if ss[i] not in p: # 一個與pattern完全不同的字
                        start += i + 1
                        break
                    else: # 一個在pattern中的字
                        start += 1
                        break # 
                else:
                    res = res.replace(ss[i],'',1)
            ''' 在出現符合p的字串後,檢驗該字串尾部的下一個位置值是否與該字串的頭部值相等,
                若相等代表位移一格後的字串也符合p,依此類推.但若該字串尾部的下一個位置值
                與該字串的頭部值不等且該值不在p中,則直接跳到該值健行搜索,否則只能位移一格
                繼續搜尋
            '''
            if res == '':
                self.index += [start]
                # print('next start index = ',start)
                print('examined sample:', s[start:start + i + 1]) 
                # 位移一個字元,檢查頭尾
                while True:
                    if start + i + 1 <= len(s) - 1: 
                        if s[start + i + 1] == s[start]:
                             start += 1
                             self.index += [start]
                             print('next start index = ',start)
                             print('examined sample:', s[start:start + i + 1]) 
                        else:
                            if s[start + i + 1] not in p:
                                start += i + 1
                                break
                            else:
                                start += 1
                                break
                    else: # 
                        return self.index
            print('next start index = ',start)
            print('examined sample:', ss) 
        return self.index
